DROP DATABASE IF EXISTS dobrolet_airline;
CREATE DATABASE dobrolet_airline;
USE dobrolet_airline;


DROP TABLE IF EXISTS airline_positions;
CREATE TABLE airline_positions (
	id SERIAL PRIMARY KEY,
	name VARCHAR(100) UNIQUE NOT NULL COMMENT 'Название должности',
	description VARCHAR(255) DEFAULT 'New position' COMMENT 'Описание должности',
	created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP	
);

DROP TABLE IF EXISTS employees;
CREATE TABLE employees (
	id SERIAL PRIMARY KEY,
	f_name VARCHAR(100) NOT NULL COMMENT 'Имя',
	m_name VARCHAR(100) COMMENT 'Отчество',
	l_name VARCHAR(100) NOT NULL COMMENT 'Фамилия',
	birthday_at DATE,
	hired_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);


DROP TABLE IF EXISTS employees_info;
CREATE TABLE employees_info (
	employee_id BIGINT UNSIGNED NOT NULL,
	phone BIGINT UNSIGNED,
	education VARCHAR(255),
	address VARCHAR(255),
	current_position_id BIGINT UNSIGNED,
	
	
	FOREIGN KEY (employee_id) REFERENCES employees(id),
	FOREIGN KEY (current_position_id) REFERENCES airline_positions(id)
);


DROP TABLE IF EXISTS plane_types;
CREATE TABLE plane_types(
	id SERIAL PRIMARY KEY,
	model VARCHAR(100) NOT NULL,
	manufactur VARCHAR(100),
	
	description VARCHAR(255),
	country VARCHAR(100),
	created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
	
);

DROP TABLE IF EXISTS airline_planes;
CREATE TABLE airline_planes (
	id SERIAL PRIMARY KEY,
	reg_num VARCHAR(10) UNIQUE NOT NULL COMMENT 'Регистрационный номер',
	plane_type_id BIGINT UNSIGNED NOT NULL,
	pas_cap_first INT UNSIGNED NOT NULL,
	pas_cap_travel INT UNSIGNED NOT NULL,
	created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
	
	FOREIGN KEY (plane_type_id) REFERENCES plane_types(id)
	
);



DROP TABLE IF EXISTS airports;
CREATE TABLE airports (
	id SERIAL PRIMARY KEY,
	icao_code VARCHAR(4) UNIQUE NOT NULL COMMENT 'ICAO код аэропорта, напр Шереметьево UUEE',
	iata_code VARCHAR(3) UNIQUE NOT NULL COMMENT 'IATA код аэропорта, напр Шереметьево SVO',
	ap_name VARCHAR(255),
	country VARCHAR(100),
	town VARCHAR(100),
	coord_long FLOAT NOT NULL,
	coord_lat FLOAT NOT NULL,
	created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
	updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
		
);


DROP TABLE IF EXISTS passangers;
CREATE TABLE passangers(
	id SERIAL PRIMARY KEY,
	f_name VARCHAR(100) NOT NULL COMMENT 'Имя',
	m_name VARCHAR(100) COMMENT 'Отчество',
	l_name VARCHAR(100) NOT NULL COMMENT 'Фамилия',
	passport_code VARCHAR(100) UNIQUE NOT NULL
);




DROP TABLE IF EXISTS flight_positions;
CREATE TABLE flight_positions (
	id SERIAL PRIMARY KEY,
	name VARCHAR(100) NOT NULL,
	description VARCHAR(255)
);













DROP TABLE IF EXISTS scheduled_flights;
CREATE TABLE scheduled_flights (
	id SERIAL PRIMARY KEY,
	flight_num INT,
	departure_airport_id BIGINT UNSIGNED NOT NULL,
	arrival_airport_id BIGINT UNSIGNED NOT NULL,
	
	FOREIGN KEY (departure_airport_id) REFERENCES airports(id),
	FOREIGN KEY (arrival_airport_id) REFERENCES airports(id)
);



DROP TABLE IF EXISTS flights;
CREATE TABLE flights (
	id SERIAL PRIMARY KEY,
	scheduled_flight_id BIGINT UNSIGNED NOT NULL,
	plane_id BIGINT UNSIGNED,
	take_off_time DATETIME,
	landing_time DATETIME,
	
	cancelled BIT DEFAULT 0,
	
	
	FOREIGN KEY (plane_id) REFERENCES airline_planes(id),
	FOREIGN KEY (scheduled_flight_id) REFERENCES scheduled_flights(id)
	
) COMMENT = 'Все выполненые полеты в а/к';


DROP TABLE IF EXISTS crew_for_flights;
CREATE TABLE flight_crew_for_flights(
	flight_id BIGINT UNSIGNED,
	crew_member BIGINT UNSIGNED,
	flight_position_id BIGINT UNSIGNED,
	
	FOREIGN KEY (flight_id) REFERENCES flights(id),
	FOREIGN KEY (crew_member) REFERENCES employees(id),
	FOREIGN KEY (flight_position_id) REFERENCES flight_positions(id)
	
);


DROP TABLE IF EXISTS pax_tickets;
CREATE TABLE pax_tickets(
	id SERIAL PRIMARY KEY,
	pax_id BIGINT UNSIGNED NOT NULL,
	flight_id BIGINT UNSIGNED NOT NULL,
	cost DECIMAL(7,2) UNSIGNED,
	class ENUM('F', 'T') COMMENT 'First, Travel',
	flight_date DATETIME,
	
	
	FOREIGN KEY (flight_id) REFERENCES scheduled_flights(id),
	FOREIGN KEY (pax_id) REFERENCES passangers(id)
);



